mapper.select:
SELECT QTY AS value
FROM INVENTORY
WHERE ITEMID = #{itemId}

mapper.update:
UPDATE INVENTORY 
SET QTY = QTY - #{increment}
WHERE ITEMID = #{itemId}

UPDATE ITEM
SET AUCTIONID = #{maxAuctionId}
WHERE ITEMID = #{itemId}

